package exec2;

public class Operacoes {

	/**
	 * Classe criada para Operações
	 * 
	 * @author vfurtado
	 * @since 11/02/2020
	 * @version 0.1
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Exemplo de adicao
		int adicao = 5 + 2;
		//Exemplo de subtração 
		int subtracao = 5 - 2;
//		Exemplo de Multiplicação
		int multiplicacao = 5 * 2;
//		Exemplo de Divisão
		int divisao = 10 / 2;
//		Exemplo de modulo 
		// Resto da Divisão 
		int modulo = 10 % 3;

		System.out.println("O resultado de 5 + 2 é: " + adicao);
		System.out.println("O resultado de  5 - 2 é: " + subtracao);
		System.out.println("O resultado de 5 * 2 é: " + multiplicacao);
		System.out.println("O resultado de 10 / 2 é: " + divisao);
		System.out.println("O resultado de 10 % 3 é: " + modulo);
		

	}

}
